func TestUnmarshalPublicKey(t *testing.T) {
	for _, c := range unmarshalPublicKeyCases {
		key, err := ioutil.ReadFile(c.path)
		if err != nil {
			panic(err)
		}
		pub, err := UnmarshalPublicKey(string(key))
		if err != nil {
			t.Error(err)
			continue
		}

		if pub.Type() != c.keyType {
			t.Fail()
		}
		if !reflect.DeepEqual(pub.Public(), c.public) {
			t.Fail()
		}
		if pub.Length() != c.length {
			t.Fail()
		}
		if pub.Comment() != c.comment {
			t.Fail()
		}
	}
}
